import React, { useState, useRef, useEffect } from "react";

export default function App() {
  const [content, setContent] = useState("");
  const [dropdownOpen, setDropdownOpen] = useState(null);

  const dropdownRefs = useRef([]);

  const handleDropdownToggle = (menu) => {
    setDropdownOpen((prev) => (prev === menu ? null : menu));
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        dropdownRefs.current.every(
          (ref) => ref && !ref.contains(event.target)
        )
      ) {
        setDropdownOpen(null);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <>
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between px-4 py-2 bg-gray-100 border-b gap-2 relative">
        <div className="flex gap-2 flex-wrap">
          {["File", "Edit", "View"].map((menu, idx) => (
            <div key={menu} className="relative" ref={(el) => (dropdownRefs.current[idx] = el)}>
              <button
                className="px-4 py-2 border rounded bg-white hover:bg-gray-100 text-sm"
                onClick={() => handleDropdownToggle(menu)}
              >
                {menu} â–¼
              </button>
              {dropdownOpen === menu && (
                <div className="absolute left-0 mt-2 w-40 bg-white shadow-md border rounded z-10">
                  {menu === "File" && (
                    <>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">New</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Open...</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Save</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Download</div>
                    </>
                  )}
                  {menu === "Edit" && (
                    <>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Undo</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Redo</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Cut</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Copy</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Paste</div>
                    </>
                  )}
                  {menu === "View" && (
                    <>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Preview</div>
                      <div className="px-4 py-2 hover:bg-gray-100 cursor-pointer">Fullscreen</div>
                    </>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>

        <div className="absolute right-4 top-2 hidden sm:block">
          <span className="text-sm text-gray-500">Draft saved</span>
        </div>
      </div>

      <div className="p-6 text-gray-700">
        <h2 className="text-xl font-semibold mb-2">Document Editor</h2>
        <textarea
          className="w-full h-64 p-4 border border-gray-300 rounded shadow focus:outline-none focus:ring focus:border-blue-300 resize-none"
          placeholder="Start typing your content here..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </div>
    </>
  );
}
