import React, { useRef } from "react";

export default function App() {
  const editorRef = useRef(null);

  const format = (command) => {
    document.execCommand(command, false, null);
  };

  const formatBlock = (blockType) => {
    document.execCommand("formatBlock", false, blockType);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Toolbar */}
      <div className="flex flex-wrap gap-2 p-3 border-b bg-white shadow-sm">
        <button onClick={() => format("bold")} className="px-3 py-1 border rounded hover:bg-gray-200">
          <b>B</b>
        </button>
        <button onClick={() => format("italic")} className="px-3 py-1 border rounded hover:bg-gray-200">
          <i>I</i>
        </button>
        <button onClick={() => format("underline")} className="px-3 py-1 border rounded hover:bg-gray-200">
          <u>U</u>
        </button>
        <button onClick={() => formatBlock("h1")} className="px-3 py-1 border rounded hover:bg-gray-200">
          H1
        </button>
        <button onClick={() => formatBlock("h2")} className="px-3 py-1 border rounded hover:bg-gray-200">
          H2
        </button>
        <button onClick={() => formatBlock("p")} className="px-3 py-1 border rounded hover:bg-gray-200">
          P
        </button>
      </div>

      {/* Editable Area */}
      <div className="p-6">
        <div
          ref={editorRef}
          contentEditable
          suppressContentEditableWarning
          className="min-h-[500px] p-4 bg-white border shadow rounded focus:outline-none prose prose-sm max-w-full"
        >
          <p>Start writing here...</p>
        </div>
      </div>
    </div>
  );
}
